mapper.update:

update
seckill
set
num = num - 1
where seckill_id = #{seckillId}
and start_time <=
#{killTime}  
and end_time  >= 
#{killTime}
and num > 0

mapper.select:
call execute_seckill(
	#{seckillId,jdbcType=BIGINT,mode = IN},
	#{phone,jdbcType=BIGINT,mode = IN},
	#{killTime,jdbcType=TIMESTAMP,mode = IN},
	#{result,jdbcType=BIGINT,mode = OUT}
)
